import junit.framework.TestCase;

public class ValidPalindromeII extends TestCase {
    public boolean validPalindrome(String s) {
        return back(s, 0, s.length() - 1, false);
    }

    private boolean back(String s, int left, int right, boolean isCharRemoved) {
        if (right - left <= 1) {
            return true;
        }

        while (s.charAt(left) == s.charAt(right) && right - left > 1) {
            left++;
            right--;
        }

        if (right - left <= 1) {
            return true;
        }

        if (isCharRemoved) {
            return false;
        }

        if (s.charAt(left + 1) == s.charAt(right)) {
            if (back(s, left + 2, right - 1, true)) {
                return true;
            }
        }

        if (s.charAt(left) == s.charAt(right - 1)) {
            if (back(s, left + 1, right - 2, true)) {
                return true;
            }
        }

        return false;
    }

    public void test() {
        testTrue("abba");
        testTrue("abcba");
        testTrue("abcdba");
        testTrue("aba");
        testTrue("abca");
        testTrue("aa");
        testTrue("ab");
        testTrue("a");
        testTrue("");
        testTrue("");
        testTrue("cupuufxoohdfpgjdmysgvhmvffcnqxjjxqncffvmhvgsymdjgpfdhooxfuupucu");

        testFalse("aabc");
        testFalse("abc");
        testFalse("abcd");
        testFalse("abbcd");
        testFalse("ababcd");
    }

    private void testTrue(String input) {
        assertTrue("Should succeed: " + input, validPalindrome(input));
    }

    private void testFalse(String input) {
        assertFalse("Should fail: " + input, validPalindrome(input));
    }
}
